<?php session_start(); include '../db.php'; if (!isset($_SESSION['user_id']) || $_SESSION['user_id'] != 4) { header("Location: ../index.php"); exit(); } if (!isset($_GET['id'])) { header("Location: admin_orders.php"); exit(); } $id = intval($_GET['id']); $stmt = $con->prepare("DELETE FROM orders WHERE id = ?"); $stmt->bind_param("i", $id); if ($stmt->execute()) { echo "<script>alert('Order deleted'); window.location='admin_orders.php';</script>"; } else { echo "<script>alert('Error deleting order'); window.location='admin_orders.php';</script>"; } ?>